INSERT INTO `email_temp` VALUES (9, 'Support (gui khach hang underpayment)', 'Vietnam-visa.com: Notification of Underpayment for [invoice]', '<div style="border: 1px solid rgb(255, 255, 255); margin: 0pt auto; padding: 0pt; width: 518px; font-size: 12px; font-family: Trebuchet MS,verdana;"><span style="background: rgb(197, 232, 251) none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; height: 70px; padding-left: 50px; color: rgb(0, 140, 211); font-size: 40px;">Vietnam-Visa.com</span>\r\n<div style="background: rgb(0, 132, 207) none repeat scroll 0% 50%; height: 36px; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; line-height: 36px; text-align: center;"><font size="+1" color="#ffffff"><strong>Notification of Underpayment for [invoice]</strong></font></div>\r\n<div style="background: rgb(197, 232, 251) none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;">\r\n<div style="border-left: 4px solid rgb(197, 232, 251); border-right: 4px solid rgb(197, 232, 251); border-bottom: 4px solid rgb(197, 232, 251); padding: 0px 30px 41px 40px; background: rgb(255, 255, 255) none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; float: left; width: 440px;">\r\n<table cellspacing="0" cellpadding="0" border="0" width="100%">\r\n    <p style="margin: 10px 20px 7px 0pt;">[time_now]</p>\r\n    <p style="margin: 10px 20px 7px 0pt;">Dear [fullname],</p>\r\n    <p style="margin: 10px 20px 7px 0pt;">You are kindly informed that your Vietnam visa application has been finished and all your information is now in our database already.</p>\r\n    <p style="margin: 10px 20px 7px 0pt;">As your arrival date is coming soon, please do not hesitate to complete 